	/*affiche les int associés aux types utilisés dans la table*/
	static void printTableTypes(String table) throws InstantiationException, IllegalAccessException, ClassNotFoundException{
		Class.forName("org.apache.derby.jdbc.EmbeddedDriver").newInstance();
		Connection connect;
		try {
			connect = DriverManager.getConnection("jdbc:derby:./db/pokedexdb");
			Statement s = connect.createStatement();
			
			//requête
			ResultSet rs = s.executeQuery("SELECT * FROM "+table);
	
			System.out.println(table);
			int nbcolumn = rs.getMetaData().getColumnCount();
			for(int i = 1; i <= nbcolumn ; i++){
				System.out.print(rs.getMetaData().getColumnName(i));
				if(i < nbcolumn){
					System.out.print(" --- ");
				}
			}			
			System.out.println();
			
			rs.next();
			for(int i = 1; i <= nbcolumn ; i++){
				//afficher le contenu de la case en fonction du type
				int typecolumn = rs.getMetaData().getColumnType(i);
				if(i < nbcolumn){
					System.out.print(typecolumn+" --- ");
				}
			}
		
		} catch (SQLException e) {
			System.out.println("Impossible de se connecter à la bdd. ");
			e.printStackTrace();
		}
			
	}
	
	
	// ---- 
	
	
	static void printTable(String table) throws InstantiationException, IllegalAccessException, ClassNotFoundException{
		Class.forName("org.apache.derby.jdbc.EmbeddedDriver").newInstance();
		Connection connect;
		try {
			connect = DriverManager.getConnection("jdbc:derby:./db/pokedexdb");
			Statement s = connect.createStatement();
			
			//requête
			ResultSet rs = s.executeQuery("SELECT * FROM "+table);
			
			//Affichage
			System.out.println(table);
			int nbcolumn = rs.getMetaData().getColumnCount();
			for(int i = 1; i <= nbcolumn ; i++){
				System.out.print(rs.getMetaData().getColumnName(i));
				if(i < nbcolumn){
					System.out.print(" --- ");
				}
			}			
			System.out.println();
			
			while(rs.next()){
				
				for(int i = 1; i <= nbcolumn ; i++){
					
					//afficher le contenu de la case en fonction du type
					int typecolumn = rs.getMetaData().getColumnType(i);
					
					switch (typecolumn) {
						case 4: 
						case 5: 
							System.out.print(rs.getInt(i));
							break;
						case 8:
							System.out.print(rs.getFloat(i));
							break;
						case 12:
							System.out.print(rs.getString(i));
							break;
						default:
							//si ca arrive, on a un probleme
							//System.out.print(rs.getObject(i));
							System.out.println("PROBLEME lors de la lecture de la table "+table+". Type de la colonne "+i+" non conforme.");
							break;
					}
					
                    //affichage séparateur
					if(i < nbcolumn){
						System.out.print("  ---  ");
					}
				}			
				System.out.println();
			}
			
			connect.close();
			
		} catch (SQLException e) {
			System.out.println("Impossible de se connecter à la bdd. ");
			e.printStackTrace();
		}
	}
	
	
